mapper.select:
select order_id, customer_id, receiver_id, order_date, order_settlement_method
from order_t
where order_id = #{orderId}

select count(*) 
from order_t
where customer_id = #{customerId}


select rnum, order_id, customer_id, receiver_id, order_date, order_settlement_method
from (
    select rownum as rnum, order_id, customer_id, receiver_id, order_date, order_settlement_method
    from (
        select order_id, customer_id, receiver_id, order_date, order_settlement_method
        from order_t
        where customer_id = #{customerId}
        order by order_id desc
    )
    where rownum <= #{pager.endRowNo}
)
where rnum >= #{pager.startRowNo}
