mapper.select:
select receiver_id, customer_id, receiver_name, receiver_zip, receiver_address, receiver_address_detail, receiver_telno, receiver_demand, receiver_address_type, RECEIVER_PERSON_NAME
from receiver
where receiver_id = #{receiverId}


select rnum, receiver_id, receiver_name, receiver_zip, receiver_address, receiver_address_detail, receiver_telno, receiver_enabled, receiver_person_name
from (
	 select rownum as rnum, receiver_id, receiver_name, receiver_zip, receiver_address, receiver_address_detail, receiver_telno, receiver_enabled, receiver_person_name
	 from (
	 	select receiver_id, receiver_name, receiver_zip, receiver_address, receiver_address_detail, receiver_telno, receiver_enabled, receiver_person_name
	 	from receiver
	 	where customer_id = #{customerId} and receiver_enabled = 'TRUE'
	 	order by receiver_id desc
	 )
	 where rownum <= #{pager.endRowNo}
)
where rnum >= #{pager.startRowNo}
