mapper.select:
select receiver_id, customer_id, receiver_name, receiver_zip, receiver_address, receiver_address_detail, receiver_telno, receiver_demand, receiver_address_type
from receiver
where receiver_id = #{receiverId}


select rnum, receiver_id, receiver_name, receiver_zip, receiver_address, receiver_address_detail, receiver_telno
from (
	 select rownum as rnum, receiver_id, receiver_name, receiver_zip, receiver_address, receiver_address_detail, receiver_telno
	 from (
	 	select receiver_id, receiver_name, receiver_zip, receiver_address, receiver_address_detail, receiver_telno
	 	from receiver
	 	order by receiver_id desc
	 )
	 where rownum <= #{endRowNo}
)
where rnum >= #{startRowNo}
